import { useState,useRef } from 'react';
import './Game.css';

const Game =({
verifyletter,
pickedWord,
pickedCategory,
letters,
guessedLetters,
wrongLetters,
guesses,
score}) =>{

  const[letter,setLetter] = useState("");
  const letterInputRef =useRef(null);
  const handleSubmit = (e) =>{
    e.preventDefault();

    verifyletter(letter);
    setLetter("");

    letterInputRef.current.focus();
  };
    return(
        <div className="game">
           <p className="points">
              <span>Pontuação: {score} </span>
           </p>
           <h1>Advinhe a palavra:</h1>
           <h3 className="tip">
             Dica sobre a Palavra:<span>{pickedCategory}</span>
           </h3>
           <p>Voce ainda tem {guesses} tentativa(s).</p>
           <div className="wordConateiner">
              {letters.map((letter,i)=>
              guessedLetters.includes(letter)? (
                <span key={i} className="letter">
                {letter}
                </span>
              ):(
                <span key={i} className="blankSquare"></span>
              )
              )}
           </div>
           <div className="letterContainer">
            <p>Tente advinhar uma letra da palavra:</p>

            <form onSubmit={handleSubmit}>
                <input type="text" name="letter" maxLength="1" required onChange={(e)=> setLetter(e.target.value)} value={letter} ref={letterInputRef}></input>
                <button onClick={verifyletter}>Jogar</button>
            </form>
           </div>
           <div className="wrongLettersContainer">
             <p>Letras ja utilizadas:</p>
             {wrongLetters.map((letter,i)=>
             (
              <span key={i}>{letter}, </span>
             )
             )}
           </div> 
           
        </div>
    )
}
export default Game;
